import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`

    $main-bg: #fff;
    $main-color: #4267b2;

    $txt-main-color: #000;
    $txt-second-color: #8d8d8d;
    $txt-white: #fff;

    $btn-main-bg: $main-color;
    $btn-main-color: #fff;

    $header-height: 170px;
    $header-tablet-height: 70px;
    $header-mobile-height: 40px;
    $header-shrink-height: 70px;

    $box-shadow: rgba(149, 157, 165, 0.2) 0px 8px 24px;

    $colors: (
        "blue": #4267b2,
        "orange": #fbb96b,
        "pink": #fe7e73,
        "white": #fff,
        "black": #000,
        "main": $main-color,
        "red": #ff0000,
    );

    $mobile-width: 600px;
    $tablet-width: 1024px;

    $numbers: (0, 1, 2, 3, 4, 5, 6, 7, 8);


    /* base */
    * {
    padding: 0;
    margin: 0;
    box-sizing: border-box;
    -webkit-tap-highlight-color: transparent;
    user-select: none;
}

html {
    font-size: 13px;

    @include mobile {
        font-size: 11px;
    }
}

body {
    font-family: "Roboto", sans-serif;
}

ul {
    list-style-type: none;
}

a {
    text-decoration: none;
    color: unset;
}

a:hover {
    color: $main-color;
}

img {
    max-width: 100%;
}

@each $color, $val in $colors {
    .color-#{$color} {
        color: $val;
    }

    .bg-#{$color} {
        background-color: $val;
    }
}

@each $val in $numbers {
    .grid-col-#{$val} {
        grid-template-columns: repeat($val, 1fr);
    }
}

@each $val in $numbers {
    .grid-col-md-#{$val} {
        @include tablet {
            grid-template-columns: repeat($val, 1fr);
        }
    }
}

@each $val in $numbers {
    .grid-col-sm-#{$val} {
        @include mobile {
            grid-template-columns: repeat($val, 1fr);
        }
    }
}

.container {
    width: 100%;
    max-width: 1620px;
    padding: 0 50px;
    margin: auto;

    @include tablet {
        padding: 0 20px;
    }

    @include mobile {
        padding: 0 10px;
    }
}

.main {
    margin-top: $header-height;
    margin-bottom: 2rem;
    min-height: 100vh;

    @include tablet {
        margin-top: calc(#{$header-tablet-height} + 20px);
    }

    @include mobile {
        margin-top: calc(#{$header-mobile-height} + 10px);
    }
}

.grid {
    display: grid;
}


/* mixin */
@mixin flex($align-item, $justify-content) {
    display: flex;
    align-items: $align-item;
    justify-content: $justify-content;
}

@mixin mobile {
    @media only screen and (max-width: $mobile-width) {
        @content;
    }
}

@mixin tablet {
    @media only screen and (max-width: $tablet-width) {
        @content;
    }
}


`;

export default GlobalStyle;
